function experiment2_display_results (results)
    % EXPERIMENT2_DISPLAY_RESULTS (results)
    %
    % Displays results generated by EXPERIMENT2_TRAIN_AND_EVALUATE().
    %
    % Input:
    %  - results: results structure, or filename. If none is provided, file
    %    selection dialog will be displayed.
    
    % Choose results
    if ~exist('results', 'var') || isempty(results)
        [ filename, pathname ] = uigetfile('*.mat', 'Pick a results file');
        if isequal(filename, 0)
            return;
        end
        results = fullfile(pathname, filename);
    end
    
    % If results are given as a filename, load them
    if ischar(results)
        results = load(results);
        results = results.results;
    end
    
    %% Print
    fprintf('\n\n');
    table_line = strjoin({'Image name', 'Num annotations', 'Distance threshold', 'Scale factor', 'Num proposals', 'Proposal ratio', 'Proposal precision', 'Proposal recall', 'Proposal F-score', 'Num detections', 'Detection ratio', 'Detection precision', 'Detection recall', 'Detection F-score\n'}, '\t');
    fprintf(table_line);
    for i = 1:numel(results)
        table_line = strjoin({'%s', '%d', '%.0f px', '%g', '%d', '%.2f %%', '%.2f %%', '%.2f %%', '%.2f %%', '%d', '%.2f %%', '%.2f %%', '%.2f %%', '%.2f %%\n'}, '\t');
        fprintf(table_line, results(i).image_name, results(i).num_annotations, results(i).distance_threshold, results(i).scale_factor, ...
            results(i).proposal_number, 100*results(i).proposal_number/results(i).num_annotations, 100*results(i).proposal_precision, 100*results(i).proposal_recall, 100*results(i).proposal_f_score, ...
            results(i).detection_number, 100*results(i).detection_number/results(i).num_annotations, 100*results(i).detection_precision, 100*results(i).detection_recall, 100*results(i).detection_f_score);
    end
    fprintf('\n\n');    
end